import UIKit
import QRScanner

class QRController: UIViewController {
    
    private lazy var qrScannerView:QRScannerView = {
        let qrScaner = QRScannerView.init(frame: CGRect.init(x: 0, y: 0, width: UIScreen.main.bounds.size.width, height: UIScreen.main.bounds.size.height));
        self.view.addSubview(qrScaner);
        qrScaner.configure(delegate: self, input: .init(isBlurEffectEnabled: true))
        return qrScaner;
    }()
    
    private lazy var flashButton:FlashButton = {
        let isFull = UIApplication.shared.statusBarFrame.size.height>20;
        let flash:FlashButton = FlashButton.init(frame: CGRect.init(x: (UIScreen.main.bounds.size.width-80)/2.0, y: UIScreen.main.bounds.size.height-80-(isFull ? 79:40), width: 80, height: 80));
        flash.addTarget(self, action: #selector(tapFlashButton), for: .touchUpInside);
        self.view.addSubview(flash);
        
        return flash;
    }()
    
    @objc func tapFlashButton(_ sender: UIButton) {
        qrScannerView.setTorchActive(isOn: !sender.isSelected)
    }
    override func viewDidDisappear(_ animated: Bool) {
        super.viewDidDisappear(animated)
        qrScannerView.stopRunning()
    }

    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        qrScannerView.startRunning()
        self.view.bringSubviewToFront(self.flashButton);
    }
    override func viewDidLoad() {
        super.viewDidLoad()
    }
}

// MARK: - QRScannerViewDelegate
extension QRController: QRScannerViewDelegate {
    func qrScannerView(_ qrScannerView: QRScannerView, didFailure error: QRScannerError) {
        print(error.localizedDescription)
    }

    func qrScannerView(_ qrScannerView: QRScannerView, didSuccess code: String) {
        if let url = URL(string: code), (url.scheme == "http" || url.scheme == "https") {
            openWeb(url: url)
        } else {
            showAlert(code: code)
        }
    }

    func qrScannerView(_ qrScannerView: QRScannerView, didChangeTorchActive isOn: Bool) {
        flashButton.isSelected = isOn
    }
}

// MARK: - Private
private extension QRController {
    func openWeb(url: URL) {
        UIApplication.shared.open(url, options: [:], completionHandler: { _ in
            self.qrScannerView.rescan()
        })
    }

    func showAlert(code: String) {
        let alertController = UIAlertController(title: code, message: nil, preferredStyle: .actionSheet)
        let copyAction = UIAlertAction(title: "Copy", style: .default) { [weak self] _ in
            UIPasteboard.general.string = code
            self?.qrScannerView.rescan()
        }
        alertController.addAction(copyAction)
        let searchWebAction = UIAlertAction(title: "Search Web", style: .default) { [weak self] _ in
            UIApplication.shared.open(URL(string: "https://www.google.com/search?q=\(code)")!, options: [:], completionHandler: nil)
            self?.qrScannerView.rescan()
        }
        alertController.addAction(searchWebAction)
        let cancelAction = UIAlertAction(title: "Cancel", style: .cancel, handler: { [weak self] _ in
            self?.qrScannerView.rescan()
        })
        alertController.addAction(cancelAction)
        present(alertController, animated: true, completion: nil)
    }
    
}
